def test_offset(stretch_case_011):
    stretch = stretch_case_011

    edge = stretch.edge('(48,52)')
    assert isinstance(edge, Edge)
    edge_seqs = edge.offset().offset(2.75, dist_tol_to_pivot=1e-2, dist_tol_to_edge=1e-2)
    assert isinstance(edge_seqs, list)
    assert all(isinstance(edge_seq, EdgeSeq) for edge_seq in edge_seqs)